function pollBalance(){var n=$("#Id").val(),t=parseFloat($("#balance").html());$.ajax({type:"GET",url:"/bankaccount/"+n,headers:{Accept:"application/json; charset=utf-8","Content-Type":"application/json; charset=utf-8"},success:function(n){t!==n.Balance&&$("#balance").html(n.Balance);setTimeout(pollBalance,5e3)}})}pollBalance();